	public static Set<String> getKanjiSet (int level)
	{
		if (level < 0 || level > 4)
			throw new IllegalArgumentException ("The level must remain between 0 and 4.") ;
		Set<String> ret = new HashSet<String> () ;
		String kanjiListStr = levels[level].intern() ;
		for (int i = 0 ; i < kanjiListStr.length() ; ++i) {
			ret.add(String.valueOf(kanjiListStr.charAt(i))) ;
		}
		return ret ;	
	}
